import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "./Welcome.css";
import backendBaseUrl from "./config";

function Welcome() {
    const location = useLocation();
    const navigate = useNavigate();
    const { name } = location.state || { name: "Guest" };  // Ensure `name` is defined
    const [output, setOutput] = useState("");
    const [permissions, setPermissions] = useState({});
    const [showScript4Input, setShowScript4Input] = useState(false);
    const [xAxisInput, setXAxisInput] = useState("");
    const [yAxisInput, setYAxisInput] = useState("");

    useEffect(() => {
        const fetchPermissions = async () => {
            try {
                const response = await axios.get(`${backendBaseUrl}/permissions/${name}`);
                setPermissions(response.data.permissions || {});  // Ensure permissions are set
            } catch (error) {
                console.error("Error fetching permissions:", error);
            }
        };
        fetchPermissions();
    }, [name]);

    const fetchScriptOutput = async (script) => {
        let ApiQuery = "";
        if (script === "script4") {
            ApiQuery = `script4?name=${name}&xplot=${xAxisInput}&yplot=${yAxisInput}`;
        }

        try {
            const response = await axios.get(`${backendBaseUrl}/run-script/${ApiQuery}`);
            setOutput(response.data.output);
            setShowScript4Input(false);
        } catch (error) {
            console.error("Error fetching script output:", error);
            setOutput("Error fetching script output");
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('user'); 
        sessionStorage.removeItem('user'); 
        navigate("/");
    };

    return (
        <div className="welcome-container">
            <div className="welcome-header">
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </div>
            <div className="message-box">
                <h1>Welcome {name}</h1>
            </div>
            <div className="button-box">
                {permissions.script4_enabled && (
                    <button onClick={() => setShowScript4Input(true)}>Script-4</button>
                )}
            </div>

            {showScript4Input && (
                <div className="input-container">
                    <label>
                        Enter the X-axis points separated by spaces:
                        <input type="text" value={xAxisInput} onChange={(e) => setXAxisInput(e.target.value)} />
                    </label>
                    <label>
                        Enter the Y-axis points separated by spaces:
                        <input type="text" value={yAxisInput} onChange={(e) => setYAxisInput(e.target.value)} />
                    </label>
                    <button onClick={() => fetchScriptOutput('script4')}>Generate Scatter Plot</button>
                </div>
            )}

            {output && (
                <div className="output-container">
                    <img src={`${backendBaseUrl}/Images/${output}`} alt="Scatter Plot" />
                </div>
            )}
        </div>
    );
}

export default Welcome;
